sys_script_include.script:
var scheduledSetValueBaseUtils = Class.create();
scheduledSetValueBaseUtils.prototype = {

    initialize: function(jobSysId) {

        if (typeof jobSysId == "string") {
            this.jobGR = this.getJobObject(jobSysId);
        } else {
            this.jobGR = jobSysId;
        }

    },



    /**
     * Get target table details from configuration
     * And update target records with given value
     */
    processJobAndSetValue: function() {

        var table = this.jobGR.getValue("target_table");
        var query = this.jobGR.getValue("query");
        var values = this.jobGR.getValue("set_values");

        try {
            this.processTargetTable(table, query, values);
        } catch (error) {
            this.addLogs("error", "Failed to process. Error: " + error.message);
        }
    },


    /**
     * Update target records with given value
     * @param1 table (string), Target table from where records needs to be updated
     * @param2 query (string), Encoded query to looks of records which needs to be updated
     * @param3 values (json string), Values which needs to be updated in target records
     */
    processTargetTable: function(table, query, values) {

        var stats = {};
        stats.processed = 0;
        stats.updated = 0;
        stats.skipped = 0;
        stats.inValidColumns = [];

        var refinedValue = "";
        var updated = false;

        if (!gs.tableExists(table)) {
            this.addLogs("error", table + " is not valid table name.");
            this.triggerNotification("Failed", table + " is not valid table name.");
            return false;
        }

        var targetGR = new GlideRecord(table);

        if (!targetGR.isEncodedQueryValid(query)) {
            targetGR.addEncodedQuery(query);
        }

        targetGR.query();

        refinedValue = this.getRefinedValueAsEncodedQuery(targetGR, values);

        if (!refinedValue) {
            this.addLogs("error", "Failed to parse values from column 'set_values'." + refinedValue);
            this.triggerNotification("Failed", "Failed to parse values from column 'set_values'." + refinedValue);
            return false;
        }

        while (targetGR.next()) {

            stats.processed++;

            targetGR.applyEncodedQuery(refinedValue.value);
            updated = targetGR.update();
            (updated) ? stats.updated++: stats.skipped++;
        }

        var message = gs.getMessage("Total: {0}, Updated: {1}, Skipped: {2}", [stats.processed, stats.updated, stats.skipped]) +
            "\nColumns Ignored: " + refinedValue.invalid;
        this.addLogs("info", "Summary : " + message);
        this.triggerNotification("Success", message);

    },


    /**
     * Convert JSON object to encoded query and skip invalid columns
     * @param1 targetGR (GlideRecord), Object of target table
     * @param2 values (JSON), Values which needs to be converted
     * return JSON {value - Refined query, invalid - Columns which are ignored from JSON}
     */
    getRefinedValueAsEncodedQuery: function(targetGR, values) {

        var query = "";
        var inValidColumns = [];

        values = JSON.parse(values);

        for (var key in values) {

            if (targetGR.isValidField(key)) {
                (query) ? query += "^" + key + "=" + values[key]: query = key + "=" + values[key];
            } else {
                inValidColumns.push(key);
            }
        }

        if (targetGR.isEncodedQueryValid(query)) {

            return {
                "value": query,
                "invalid": (inValidColumns.length > 0) ? inValidColumns.join() : "NA"
            };

        }

        return false;
    },


    /**
     * Get set value job object from sys_id
     * @param1 jobSysId (string), Job sys_id
     * return GlideRecord of target job
     */
    getJobObject: function(jobSysId) {

        var jobGR = new GlideRecord("x_61049_set_value_jobs");

        if (jobGR.get(jobSysId)) {
            return jobGR;
        }

        return false;
    },


    /**
     * Verify and trigger notification about job summary
     * @param1 parm1, Parameter one which needs to updated in event
     * @param2 parm2, Parameter two which needs to updated in event
     */
    triggerNotification: function(parm1, parm2) {

        if (this.jobGR.getValue("notify") == 1) {
            gs.eventQueue("x_61049_set_value.execution_summary", this.jobGR, parm1, parm2);
        }

    },


    /**
     * Add logs to app log table
     * @param1 type (string), Log type error or info
     * @param2 message (string), Message which needs to be added in the log
     */
    addLogs: function(type, message) {

        var prefix = "Job name: ";
        prefix += this.jobGR.getValue("name");

        if (type == "error") {
            gs.error(prefix + "\n" + message);
        } else {
            gs.info(prefix + "\n" + message);
        }

    },

    type: 'scheduledSetValueBaseUtils'
};
